mapper.select:
SELECT od.id AS orderId, od.created_time AS createdTime, od.pay_method AS payMethod, od.real_pay_amount AS realPayAmount, od.post_amount AS postAmount, od.is_comment AS isComment, os.order_status AS orderStatus FROM orders od LEFT JOIN order_status os ON od.id = os.order_id WHERE od.user_id = #{paramsMap.userId} AND od.is_delete = 0
AND os.order_status = #{paramsMap.orderStatus}
ORDER BY od.updated_time
